sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var status = 'shall no longer receive daily task update notification.<br/><br/><br/>Please send an email to demoplace+subscribe@service-now.com to subscribe again.';

	if(current.u_active)
		status = 'are now subscribed to receive daily notifications about tasks assigned to you, and have an option to update or create new tasks by just replying to the mail. Thank you and welcome onboard!!<br/><br/><br/>To unsubscribe send an email to demoplace+unsubscribe@service-now.com';
	email.setSubject('Re: '+event.parm2.toString());

	template.print('Hi ' + current.u_first_name + ',<br/><br/>You ' + status);

})(current, template, email, email_action, event);
