sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here

	var response = JSON.parse(event.parm2);
	email.setSubject('Task Updation Status');
	var endpointBase = 'https://' + gs.getProperty('x_snc_easytask.surf_instance_name') + '.service-now.com/api/now/table/';
	var restMessage = new sn_ws.RESTMessageV2();
	var restMessageDetails = new RESTMessageSetup();
	restMessage.setBasicAuth(restMessageDetails.getUserName(),restMessageDetails.getPWD());
	restMessage.setHttpMethod('get');
	/*restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/sys_user');
	restMessage.setQueryParameter('sysparm_query','email='+event.parm1);
	var managerDetails = JSON.parse(restMessage.execute().getBody()).result[0].manager.link;
	restMessage.setEndpoint(managerDetails);
	var managerEmail = JSON.parse(restMessage.execute().getBody()).result.email;
	gs.info("Manager: "+managerEmail);
	email.addAddress('cc',managerEmail);*/
	template.print('<table border="0" cellpadding="0" cellspacing="0" height="100%" width="100%" id="bodyTable"><tr><td align="left" valign="top"><table cellpadding="0" cellspacing="0" width="700px">');
	var count = 0,countDefect = 0,countIncident = 0;
	var border = 'border: 1px solid #0368d4;';
	var webKitBorder = '-webkit-border-radius: 3px;';
	var mozBorder = '-moz-border-radius: 3px;';
	var padding = 'padding: 5px;';
	var max_width = 'max-width: 250px;'; 
	var display = 'display:table-row;';
		template.print('<p> The following tasks have been updated/created by you!!</p><tr style = "'+display+'">');
	for(var _response in response.ScrumTasks){
		var obj = response.ScrumTasks[_response];
		for (var key in obj){
			restMessage.setEndpoint(obj[key].payload.story);
			var story = JSON.parse(restMessage.execute().getBody()).result.short_description.substring(0,14);
			if(count > 2){
				template.print('</tr><tr style = "'+display+'">');
				count = 0;
			}
			template.print('<td style="'+border+webKitBorder+padding+mozBorder+max_width+'"><p></p><p>&nbsp; Task: <a href=\"https://surfdev.service-now.com/nav_to.do?uri=rm_scrum_task.do?sys_id='+obj[key].payload.sys_id+'%26sysparm_view=Scrum\">'+obj[key].payload.number+'</a></p>');
			template.print('<p>&nbsp; Story: '+story+'...</p>');
			template.print('<p>&nbsp; Task type: '+obj[key].payload.type+'</p>');
			template.print('<p>&nbsp; Short Description: '+obj[key].payload.short_description.substring(0,14)+'...</p>');
			template.print('<p>&nbsp; State: '+obj[key].payload.state+'</p>');
			template.print('<p>&nbsp; Planned Hours: '+obj[key].payload.planned_hours+'</p>');
			template.print('<p>&nbsp; Actual Hours: '+obj[key].payload.hours+'</p><p></p></td>');
			count++;
		}
	}
	template.print('</tr></table><br\>');
	template.print('<table cellpadding="0" cellspacing="0" width="700px"><tr style = "'+display+'">');
	for(var _defect in response.Defects){
		var _obj = response.Defects[_defect];
		for (var _key in _obj){
			restMessage.setEndpoint(_obj[_key].payload.story);
			var _defectstory = JSON.parse(restMessage.execute().getBody()).result.short_description.substring(0,14);
			if(countDefect > 2){
				template.print('</tr><tr style = "'+display+'">');
				countDefect = 0;
			}
			template.print('<td style="'+border+webKitBorder+padding+mozBorder+max_width+'"><p></p><p>&nbsp; Defect: <a href=\"https://surfdev.service-now.com/nav_to.do?uri=rm_defect.do?sys_id='+_obj[_key].payload.sys_id+'%26sysparm_view=Scrum\">'+_obj[_key].payload.number+'</a></p>');
			template.print('<p>&nbsp; Story: '+_defectstory+'...</p>');
			template.print('<p>&nbsp; Short Description: '+_obj[_key].payload.short_description.substring(0,14)+'...</p>');
			template.print('<p>&nbsp; State: '+_obj[_key].payload.state+'</p>');
			template.print('<p>&nbsp; Actual Hours: '+_obj[_key].payload.hours+'</p><p></p></td>');
			countDefect++;
		}
	}
	template.print('</tr></table><br\>');
	template.print('<table cellpadding="0" cellspacing="0" width="700px"><tr style = "'+display+'">');
	for(var incident in response.Incidents){
		var _incobj = response.Incidents[incident];
		for (var _inckey in _incobj){
			if(countIncident > 2){
				template.print('</tr><tr style = "'+display+'">');
				countIncident = 0;
			}
			template.print('<td style="'+border+webKitBorder+padding+mozBorder+max_width+'"><p></p><p>&nbsp; Incident: <a href=\"https://surfdev.service-now.com/nav_to.do?uri=incident.do?sys_id='+_incobj[_inckey].payload.sys_id+'%26sysparm_view=Scrum\">'+_incobj[_inckey].payload.number+'</a></p>');
			template.print('<p>&nbsp; Short Description: '+_incobj[_inckey].payload.short_description.substring(0,14)+'...</p>');
			template.print('<p>&nbsp; State: '+_incobj[_inckey].payload.state+'</p>');
			template.print('<p>&nbsp; Actual Hours: '+_incobj[_inckey].payload.hours+'</p><p></p></td>');
			countIncident++;
		}
	}
	template.print('</tr></table></td></tr></table>');
})(current, template, email, email_action, event);
