sys_script_include.script:
function updatePayloadTable(response){
	for (var _response in response){
		var gr = new GlideRecord('x_snc_subscribed_f_mailresponsepayload');
		gr.addQuery('u_task_id','=',response[_response].payload.number.toString());
		gr.query();
		if(gr.hasNext()){
			while(gr.next()){
				gr.u_response = response[_response].status;
				gr.u_payload = JSON.stringify(response[_response].payload);
				gr.u_usersys_id = response[_response].sys_id;
				gr.u_hours = response[_response].payload.hours;
				gr.update();
			}
		}
		else{
			gr.u_response = response[_response].status;
			gr.u_payload = JSON.stringify(response[_response].payload);
			gr.u_usersys_id = response[_response].sys_id;
			gr.u_hours = response[_response].payload.hours;
			gr.insert();
		}
	}	
}
