sysauto_script.script:
var scheduler = {};
scheduler.getTasksByAssignedTo = function(){
	var restMessageDetails = new RESTMessageSetup();
	var restMessage = new sn_ws.RESTMessageV2();
	var endpointBase = 'https://' + gs.getProperty('x_snc_easytask.surf_instance_name') + '.service-now.com/api/now/table/';
	restMessage.setBasicAuth(restMessageDetails.getUserName(),restMessageDetails.getPWD());
	restMessage.setHttpMethod('get');
	var gr = new GlideRecord('x_snc_easytask_subscribedforemailnotifications');
	gr.addQuery('u_active',true);
	gr.query();
	var _task,_defect,_incident,result,_userTasks,_userDefects,_userIncidents;
	while(gr.next()){
		restMessage.setEndpoint(endpointBase + 'rm_scrum_task');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+gr.u_devsysid+'^ORDERBYnumber');
		result = restMessage.execute();

		if(result && result.getBody()) 
			_userTasks = JSON.parse(result.getBody());


		/*restMessage = new sn_ws.RESTMessageV2();
		restMessage.setBasicAuth(restMessageDetails.getUserName(),restMessageDetails.getPWD());
		restMessage.setHttpMethod('get');
		restMessage.setEndpoint(endpointBase + 'rm_defect');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+gr.u_devsysid+'^ORDERBYnumber');
		result = restMessage.execute();

		if(result && result.getBody()) 
			_userDefects = JSON.parse(result.getBody());


		restMessage = new sn_ws.RESTMessageV2();
		restMessage.setBasicAuth(restMessageDetails.getUserName(),restMessageDetails.getPWD());
		restMessage.setHttpMethod('get');
		restMessage.setEndpoint(endpointBase + 'incident_task');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+gr.u_devsysid+'^ORDERBYnumber');
		result = restMessage.execute();

		if(result && result.getBody()) 
			_userIncidents = JSON.parse(result.getBody());*/

		var user = gr.u_email;

		try{
			_task = getScrumTasks(_userTasks);
		}
		catch(err){
			gs.error('Tasks Not Found: '+ err.toString());
			_task = {};
		}

		try{
			_defect = getDefects(_userDefects);
		}
		catch(err){
			gs.error('Defects Not Found: '+ err.toString());
			_defect = {};
		}

		try{
			_incident = getIncidents(_userIncidents);
		}
		catch(err){
			gs.error('Incidents Not Found: '+ err.toString());
			_incident = {};
		}

		var tasks = {
			first_name: gr.u_first_name.toString(),
			scrumTasks: _task,
			defects: _defect,
			incidents: _incident
		};
		//Event is firing
		gs.eventQueue('x_snc_easytask.logWorkHours', current, user, JSON.stringify(tasks));

	}
};

scheduler.getTasksByAssignedTo();
