sysauto_script.script:
var scheduler = {};
scheduler.getTasksByAssignedTo = function(){
	var restMessage = new sn_ws.RESTMessageV2();
	restMessage.setBasicAuth("souvik.ghosh","iWcMyPwde4d!");
	restMessage.setHttpMethod('get');
	restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/x_snc_tasklogfrome_subscribedformail');
	restMessage.setQueryParameter('sysparm_query','u_email_tasks=1');
	var response = restMessage.execute();
	var parser = JSON.parse(response.getBody());
	var _task,_defect,_incident;
	for (var i =0; i<parser.result.length; i++){
		restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/rm_scrum_task');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+parser.result[i].u_user_subscribed.value);
		var _userTasks = JSON.parse(restMessage.execute().getBody());
		restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/rm_defect');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+parser.result[i].u_user_subscribed.value);
		var _userDefects = JSON.parse(restMessage.execute().getBody());
		restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/incident');
		restMessage.setQueryParameter('sysparm_query','active=true^assigned_to='+parser.result[i].u_user_subscribed.value);
		var _userIncidents = JSON.parse(restMessage.execute().getBody());
		restMessage.setEndpoint(parser.result[i].u_user_subscribed.link);
		var _userDetails = JSON.parse(restMessage.execute().getBody());
		var user = _userDetails.result.email.toString();
		try{
			_task = getScrumTasks(_userTasks,restMessage);
		}
		catch(err){
			_task = {};
		}
		try{
			_defect = getDefects(_userDefects,restMessage);
		}
		catch(err){
			_defect = {};
		}
		try{
			_incident = getIncidents(_userIncidents,restMessage);
		}
		catch(err){
			_incident = {};
		}
		var tasks = {
			first_name: _userDetails.result.first_name.toString(),
			scrumTasks: _task,
			defects: _defect,
			incidents: _incident
		};
		//Event is firing
		gs.eventQueue('x_snc_subscribed_f.logWorkHours', current, JSON.stringify(user), JSON.stringify(tasks));

	}
};

scheduler.getTasksByAssignedTo();
