function syncTasks(){
	var restMessage = new sn_ws.RESTMessageV2();
	restMessage.setBasicAuth('souvik.ghosh','iWcMyPwde4d!');
	restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/core_company');
	restMessage.setQueryParameter('sysparm_query','name\=Service\-now.com');
	restMessage.setHttpMethod('get');
	var company = JSON.parse(restMessage.execute().getBody());
	restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/u_employee');
	restMessage.setQueryParameter('sysparm_query','active=true^u_manager_role=true^comapny='+company.result[0].sys_id+'^country=IN^emailLIKEChaitanya.Saragadam');
	restMessage.setQueryParameter('sysparm_fields','email,first_name,sys_id');
	var allManagersOfServiceNowIndia = JSON.parse(restMessage.execute().getBody());
	for(var i =0; i<allManagersOfServiceNowIndia.result.length;i++){
		var tasks = getManagerDelegatesTasks(restMessage,allManagersOfServiceNowIndia.result[i].sys_id);
		for(var _taskOwner in tasks){
			var _task = tasks[_taskOwner];
			for (var _i = 0; _i < _task.length; _i++){
				var gr = new GlideRecord('x_snc_subscribed_f_mailresponsepayload');
				gr.addQuery('u_task_id','=',_task[_i].number);
				gr.query();
				if(gr.hasNext()){
					while(gr.next()){
						gr.u_previous_hours = gr.u_hours;
						gr.u_previous_state = gr.u_state;
						gr.u_state = Number(_task[_i].state);
						gr.u_hours = Number(_task[_i].hours);
						gr.update();
					}
				}
				else{
					gr.initialize();
					gr.u_task_id = _task[_i].number;
					gr.u_previous_hours = 0;
					gr.u_previous_state = -6;
					gr.u_state = Number(_task[_i].state);
					gr.u_hours = Number(_task[_i].hours);
					gr.insert();
				}
			}
		}
	}		
}
syncTasks();
