sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Implement email action here
	var active = true;
	if(email.to.indexOf('+subscribe') == -1)
		active = false;
	var restMessage = new sn_ws.RESTMessageV2();
	restMessage.setBasicAuth('souvik.ghosh','iWcMyPwde4d!');
	restMessage.setEndpoint('https://surfdev.service-now.com/api/now/table/sys_user');
	restMessage.setQueryParameter('sysparm_query','active=true^email='+email.from);
	restMessage.setQueryParameter('sysparm_fields','first_name,sys_id');
	restMessage.setHttpMethod('get');
	var _user = JSON.parse(restMessage.execute().getBody());
	var gr = new GlideRecord('x_snc_easytask_subscribedforemailnotifications');
	gr.addQuery('u_devsysid',_user.result[0].sys_id);
	gr.query();
	if(gr.hasNext()){
		gr.next();
		gr.u_active = active;
		gr.update();
	}
	else{
		gr.initialize();
		gr.u_active = active;
		gr.u_devsysid = _user.result[0].sys_id;
		gr.u_email = email.from;
		gr.u_first_name = _user.result[0].first_name;
		gr.insert();
	}
	gs.eventQueue('x_snc_easytask.subscriptionResponse',gr,email.from,email.subject);

})(current, event, email, logger, classifier);
