sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {
	// Implement email action here

	var regexFromMobile= new RegExp(/STSK\d{7};[\w+]*;[\w\s&;]+(\.{3})*;[\w\s]+(\.{3})*;(\d|-\d);\d+;\d+;/gm);
	var newTaskFromMobile = new RegExp(/new_task\d;[\w+]*;STRY\d{7};[\w\s]+;(\d|-\d);\d+;\d+;/gm);
	var regexForDefect = new RegExp(/(id\=\"DFCT\d{7}\")[><\w\s=\"\-:\/,;]*<\/td>/gm);
	var regexForDefectMobile = new RegExp(/DFCT\d{7};[\w+]*;[\w\s]+(\.{3})*;[\w\s]+(\.{3})*;(\d|-\d);\d+;\d+;/gm);
	var regexForIncident = new RegExp(/(id\=\"INC\d{7}\")[><\w\s=\"\-:\/,;]*<\/td>/gm);
	var regexForIncidentMobile = new RegExp(/INC\d{7};[\w+]*;[\w\s]+(\.{3})*;[\w\s]+(\.{3})*;(\d|-\d);\d+;\d+;/gm);
	var regexfromEmail = new RegExp(/(id\=\"STSK\d{7}\")[><\w\s=\"\-:\/,;]*<\/td>/gm);
	var regexToParseNewTask = new RegExp(/(id\=\"new_task\d\")[><\w\s=\"\-:\/,;]*<\/td>/gm);
	var formEmailResponseJSON = extractJSONFromEmailResponse(email.body_html,regexFromMobile,regexfromEmail);
	var createTasksJSON = extractJSONFromEmailResponse(email.body_html,newTaskFromMobile,regexToParseNewTask);
	var formDefectResponse = extractJSONFromEmailResponse(email.body_html,regexForDefectMobile,regexForDefect);
	var incidentResponseJSON = extractJSONFromEmailResponse(email.body_html,regexForIncidentMobile,regexForIncident);
	gs.info(JSON.stringify(createTasksJSON));
	var _response_incident = updateIncidentsViaRest(incidentResponseJSON);
	updatePayloadTable(_response_incident);
	var _response_defect = updatesDefectsViaRest(formDefectResponse);
	updatePayloadTable(_response_defect);
	var _response_task = updateTasksViaRest(formEmailResponseJSON);
	updatePayloadTable(_response_task);
	var _response_new_task = createTasksViaRest(createTasksJSON, email.from);
	updatePayloadTable(_response_new_task);
	var response = {
		ScrumTasks: {
			response_task: _response_task,
			response_new_task: _response_new_task
		},
		Defects: {
			response_defect : _response_defect
		},
		Incidents: {
			response_incident : _response_incident
		}
	};
	gs.eventQueue('x_snc_easytask.acknowledgeTaskRespon', current, email.from, JSON.stringify(response));
})(current, event, email, logger, classifier);
